// src/HotelList.js
import React, { useState, useEffect } from 'react';
import './HotelList.css'; // Import the CSS file for styling

const HotelList = () => {
  const [hotels, setHotels] = useState([]);

  // Mock data to simulate fetching from an API
  useEffect(() => {
    const mockData = [
      {
        hotelId: '1',
        hotelName: 'Grand Hotel',
        hotelAddress: '123 Main St, New York, NY',
        hotelCity: 'New York',
        hotelRating: '4.5',
        hotelPhone: '1234567890',
        hotelMapLocationLink: 'https://maps.google.com',
        hotelVlogVideoLink: 'https://youtube.com',
        vlogVideoViewCount: '1000',
        vlogPostDate: '2023-08-01',
        verified: false,
        isActive: true
      },
      // More hotel objects...
    ];
    setHotels(mockData);
  }, []);

  const handleApprove = (hotelId) => {
    // Approve logic can go here
    console.log(`Hotel with ID ${hotelId} approved.`);
    alert(`Hotel with ID ${hotelId} approved.`);
  };

  return (
    <div className="hotel-list-container">
      <h1>Hotel List</h1>
      <div className="hotel-list">
        {hotels.map((hotel) => (
          <div className="hotel-card" key={hotel.hotelId}>
            <h2>{hotel.hotelName}</h2>
            <p><strong>Address:</strong> {hotel.hotelAddress}</p>
            <p><strong>City:</strong> {hotel.hotelCity}</p>
            <p><strong>Rating:</strong> {hotel.hotelRating}</p>
            <p><strong>Phone:</strong> {hotel.hotelPhone}</p>
            <p><strong>Map Location:</strong> <a href={hotel.hotelMapLocationLink} target="_blank" rel="noopener noreferrer">View on Map</a></p>
            <p><strong>Vlog Video:</strong> <a href={hotel.hotelVlogVideoLink} target="_blank" rel="noopener noreferrer">Watch Video</a></p>
            <p><strong>View Count:</strong> {hotel.vlogVideoViewCount}</p>
            <p><strong>Post Date:</strong> {hotel.vlogPostDate}</p>
            <button className="approve-button" onClick={() => handleApprove(hotel.hotelId)}>Approve</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default HotelList;
