// src/HotelList.js
import React, { useState, useEffect } from 'react';
import './HotelList.css'; // Import the CSS file for styling

const HotelList = () => {
  const [hotels, setHotels] = useState([]);

  // Mock data to simulate fetching from an API
  useEffect(() => {
    const mockData = [
      {
        hotelId: '1',
        hotelName: 'Grand Hotel',
        hotelAddress: '123 Main St, New York, NY',
        hotelCity: 'New York',
        hotelRating: '4.5',
        hotelPhone: '1234567890',
        hotelMapLocationLink: 'https://maps.google.com',
        hotelVlogVideoLink: 'https://youtube.com',
        vlogVideoViewCount: '1000',
        vlogPostDate: '2023-08-01',
        verified: false,
        isActive: true,
      },
      {
        hotelId: '2',
        hotelName: 'Coastal Resort',
        hotelAddress: '456 Beach Ave, Miami, FL',
        hotelCity: 'Miami',
        hotelRating: '4.7',
        hotelPhone: '0987654321',
        hotelMapLocationLink: 'https://maps.google.com',
        hotelVlogVideoLink: 'https://youtube.com',
        vlogVideoViewCount: '2000',
        vlogPostDate: '2023-07-15',
        verified: false,
        isActive: true,
      },
      // More hotel objects...
    ];
    setHotels(mockData);
  }, []);

  const handleApprove = (hotelId) => {
    // Approve logic can go here
    console.log(`Hotel with ID ${hotelId} approved.`);
    alert(`Hotel with ID ${hotelId} approved.`);
  };

  return (
    <div className="hotel-list-container">
      <h1>Hotel List</h1>
      <table className="hotel-table">
        <thead>
          <tr>
            <th>Name</th>
            <th>Address</th>
            <th>City</th>
            <th>Rating</th>
            <th>Phone</th>
            <th>Map Location</th>
            <th>Vlog Video</th>
            <th>View Count</th>
            <th>Post Date</th>
            <th>Approve</th>
          </tr>
        </thead>
        <tbody>
          {hotels.map((hotel) => (
            <tr key={hotel.hotelId}>
              <td>{hotel.hotelName}</td>
              <td>{hotel.hotelAddress}</td>
              <td>{hotel.hotelCity}</td>
              <td>{hotel.hotelRating}</td>
              <td>{hotel.hotelPhone}</td>
              <td>
                <a href={hotel.hotelMapLocationLink} target="_blank" rel="noopener noreferrer">View on Map</a>
              </td>
              <td>
                <a href={hotel.hotelVlogVideoLink} target="_blank" rel="noopener noreferrer">Watch Video</a>
              </td>
              <td>{hotel.vlogVideoViewCount}</td>
              <td>{hotel.vlogPostDate}</td>
              <td>
                <button className="approve-button" onClick={() => handleApprove(hotel.hotelId)}>Approve</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default HotelList;
